              .set('compress', true)
              .use(nib());

          return stylObj;
        }
      });
    },

    /**
     * Process widget stylus
     * @param str
     * @param cb
     */

    getWidgetCSS: function (str, cb) {
      getStylusObject(str).render(cb);
    }
  };

})();

